const prompt = require('prompt-sync')()
function plus(left_operand, right_operand){
    let ans = left_operand + right_operand
}
function minus(left_operand, right_operand){
    let ans =  right_operand - left_operand
}
function divide(left_operand, right_operand){
    let ans = right_operand / left_operand
    return ans
}
function times(left_operand, right_operand){
    let ans = right_operand * left_operand
    return ans
}
var num1 = parseInt(prompt("Enter left operand: "))
var num2 = parseInt(prompt("Enter right operand: "))
let operator = prompt("select your opeerartor(+, -, *, /.): ")
if(operator == '+') console.log(plus(num1, num2))
if(operator == '-') console.log(minus(num1, num2))
if(operator == '/') console.log(divide(num1, num2))
if(operator == '*') console.log(times(num1, num2))
let add = (a, b) => a + b;
console.log(add(5, 3));
