function plus(left_operand, right_operand){ return  left_operand + right_operand}
function minus(left_operand, right_operand){return right_operand - left_operand}
function divide(left_operand, right_operand){return right_operand / left_operand}
function times(left_operand, right_operand){return right_operand * left_operand}
